<?php   $itemvalue=($_POST["data"]);			$con = mysql_connect("localhost","alienper_root","kokikoki");			if (!$con)			  {			  die('Could not connect: ' . mysql_error());			  }	        $idMax=0;			mysql_select_db("alienper_feydom", $con);			$id = mysql_query("SELECT max(id) FROM $itemvalue ");		    while($row = mysql_fetch_array($id))		    {				 $idMax=$row['max(id)'];			}			mysql_query("DELETE FROM $itemvalue  where id='".$idMax."' ");			mysql_close($con);?>